catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        amountSetting(newValue);
    }

    amountSetting(newValue);
}
function amountSetting(newValue){
// alert('enter'+newValue);
if (newValue == 'Pat on the Back Award') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 300');
}
if (newValue == 'On-Spot Award') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 400');
}
if (newValue == 'Best Employee of the month') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 500');
}
if (newValue == 'Successful Go-Live Appreciation') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 250');
}
if (newValue == 'Escalation Handling Appreciation') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 200');
}
if (newValue == 'Best Newcomer Award') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 600');
}
if (newValue == 'Rising Star Award') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 800');
}
if (newValue == 'Extraordinary Employee Award') {
    g_form.setValue('u_amount_to_be_added', 'Rs. 1000');
}

//Type appropriate comment here, and begin script below

}
