sys_script_include.script:
var ManagerDetail = Class.create();
ManagerDetail.prototype = {
    initialize: function() {},
    mail: function(user) {
		gs.log('mail - entr');
		var id=user;
        var gr = new GlideRecord('sys_user');
        gr.addQuery('sys_id', id);
        gr.query();
        var mails = 'No value';
        if (gr.next()) {
            mails = gr.email;
        }
		gs.log('mail - '+mails);
        return mails;

    },

    phone: function(user) {
		var id=user;
        var gr = new GlideRecord('sys_user');
        gr.addQuery('sys_id', id);
        gr.query();
        var phones = '';
        if (gr.next()) {
            phones = gr.mobile_phone;
        }
        return phones;

    },
    type: 'ManagerDetail'
};
